DEBUG=True

fwhm_coef=2.*math.sqrt(2.*math.log(2.))
sqrt_2pi=math.sqrt(2.*math.pi)
sqrt_2 = math.sqrt(2.)
pi_d_2 = math.pi/2.

def gauss(x, x0, A, fwhm):
    sigma = fwhm/fwhm_coef
    _c=A  #/(sigma*sqrt_2pi)
    _=((x-x0)**2)/(2*sigma**2)
    # print _, _c
    _=np.exp(-_)*_c
    return _

def gauss_square(A, fwhm):
    sigma = fwhm/fwhm_coef
    _e = A*sigma*sqrt_2pi
    return _e

def arccot(x):
    return pi_d_2-np.arctan(x)

